N, K = map(int, input('N, K:\t').split())#  Check N and Kif N > 100:    N = 100if N < 1:    N = 1if K < 1:    K = 1if K > N:    K = NK -= 1matrix = []vertices = []used = [False for i in range(N)]def dfs(matrix, vertex_id, vertices, used):    vertices.append(vertex_id)    used[vertex_id] = True    vertices_amount = len(matrix)    for next_vertex_id in range(vertices_amount):        if bool(matrix[vertex_id][next_vertex_id]) and not used[next_vertex_id]:            dfs(matrix, next_vertex_id, vertices, used)if __name__ == "__main__":    counter = 0    while counter < N:        input_vector = list(map(int, input(f"{counter + 1}-я строка:\t").split()))        if (len(input_vector) != N):            print('Не верный ввод строки!')            continue        matrix.append(input_vector)        counter += 1    dfs(matrix, K, vertices, used)    print(len(vertices))