import numpy as npif __name__ == "__main__":    n = int(input('n:\t'))    if n > 100:        n = 100    if n < 2:        n = 2    matrix = np.zeros((n, n))    oriented = True    counter = 0    while counter < n:        input_vector = np.array(list(map(int, input(f"{counter+1}-я строка:\t").split())))        if (len(input_vector) != n):            print('Не верный ввод строки!')            continue        matrix[counter] = input_vector        counter += 1    for i in range(n):        for j in range(n):            if matrix[i][j] != matrix[j][i]:                oriented = False    print(matrix)    print("YES" if oriented else "NO")