import numpy as npif __name__ == "__main__":    N, K = map(int, input('N, K:\t').split())    #  Check N and K    if N > 100:        N = 100    if N < 1:        N = 1    if K < 1:        K = 1    if K > N:        K = N    calls = np.zeros((N, N))    counter = 0    while counter < N:        input_vector = np.array(list(map(int, input(f"{counter + 1}-я строка:\t").split())))        if (len(input_vector) != N):            print('Не верный ввод строки!')            continue        calls[counter] = input_vector        counter += 1    vector_calls = calls[K - 1]    vector_calls = np.delete(vector_calls, K - 1)    most_calls = vector_calls.argmax() + 1    most_calls = most_calls + 1 if most_calls >= K else most_calls    print(calls)    print()    print(most_calls)